import React, { useRef, useState } from 'react'
import { View, Text, StyleSheet, Button, Alert } from 'react-native'
import Card from '../components/Card';
import colors from '../constants/colors';

const Game = ({exclude}) => {
    const [gusNum, setGusNum] = 
        useState(genRndBtwn(0,99,exclude));
    const [step, setStep] = useState(1);
    const highestNum = useRef(99);
    const lowestNum  = useRef(0);
    
    const nextGuess = (str) => {
        if(gusNum === exclude){
            return;
        } else if((str === "lower" && gusNum < exclude)||
            (str==="greater" && gusNum > exclude)){
                Alert.alert('Don\'t Lie', 
                'you input wrong answer',[{
                    text:'Sorry', style:'cancel'
                }])
        } else {
            str === "lower" ? highestNum.current = gusNum : lowestNum.current = gusNum;

            setStep(step+1);
        }      
    }

    return (
        <View style={styles.screen}>
            <Card style={styles.card}>
                <Text style={styles.title}>Computer Guess</Text>
                <View style={{flexDirection:'row-reverse'}}>
                    <Text>Step {step} - </Text>
                    <Text></Text>
                </View>
                <View style={styles.num}>
                    <Text style={{fontSize:20, fontWeight:'700',color:colors.secend}}>
                        {gusNum}
                    </Text>
                </View>
                <View style={styles.row}>
                    <View style={styles.btn}>
                        <Button 
                        color={colors.secend}
                        title="LOWER"
                        onPress={nextGuess.bind(this,"lower")}
                        />
                    </View>
                    <View style={styles.btn}>
                        <Button 
                        title="GREATER"
                        color={colors.primary}
                        onPress={nextGuess.bind(this,"greater")}
                    />
                    </View>
                </View>
            </Card>
        </View>
    )
}

export default Game

const styles = StyleSheet.create({
    screen:{
        flex:1,
        justifyContent:'center',
        alignItems:'center'
    },  
    title:{
        fontSize:20,
        fontWeight:'700'
    },
    btn:{
        width:80
    },
    row:{
        flexDirection:'row-reverse',
        width:190,
        justifyContent:'space-between',
        paddingHorizontal:10
    },
    num:{
        borderColor:colors.secend, 
        borderWidth:2,
        borderRadius:20, 
        height:40, 
        marginVertical:15, 
        justifyContent:'center',
        alignItems:'center',
        paddingHorizontal:5
    },
    card:{
        paddingHorizontal:10,
        alignItems:'center'
    }
});


/**
 * @description Function that return an intenger between min and max (not including max)
 */
function genRndBtwn(min, max) {
    const num = Math.floor(Math.random() * (max - min)) + min;
    return num;
}
