import React, { useState } from 'react';
import * as EL from 'react-native';
import Header from './components/Header';
import Start from './screens/Start';
import Game from './screens/Game';

export default function App() {
  const [isStatrt, setIsStart] = useState(true);
  const [selNum, setSelNum] = useState(0); 

  return (
  <EL.View style={styles.screen}>
    <EL.StatusBar backgroundColor="#c41d60"/>
    <Header title={<EL.View style={{flexDirection:'row-reverse'}}>
        <EL.Text style={{color:'black', fontSize:18}}>
          Guess Number
        </EL.Text>
        <EL.Text style={{fontSize:15}}>{selNum}</EL.Text>
        <EL.Text onPress={()=>setIsStart(true)} style={{fontWeight:'bold',fontSize:20}}>â†º{'  '}</EL.Text>
      </EL.View>}/>
    {
      isStatrt ? 
       <Start onStart={(num)=>{
        setIsStart(false);
        setSelNum(num);
       }}/> :
       <Game exclude={selNum}/>
    }

  </EL.View>
  );
}

const styles = EL.StyleSheet.create({
  screen:{
    flex:1
  }
});
