import React from 'react';
import {Text, View, Button, CheckBox}  from 'react-native';

export default function TaskItems({allTasks, changeTaskDone, deleteTask, styles}) {
    return (<View  style={{flex:1, alignItems:'center',padding:40}}>
    {
      allTasks.map((t,i)=>(<View key={i} style={{...styles.container, ...styles.task, backgroundColor:t.done ? 'rgb(33, 150, 243)':'red'}}>
        <CheckBox value={t.done} onValueChange={() => changeTaskDone(t)}/>
        <Text style={{margin:10,color:'white'}}>{t.task}</Text>
        <Button title="x" color={t.done ? 'rgb(33, 150, 243)':'red'} onPress={()=>deleteTask(t.id)}/>
      </View>))
    }
    </View>)
}
